import React from 'react'
import { AutoColumn } from '../Column'
import { RowBetween } from '../Row'
import styled from 'styled-components'
import { TYPE, StyledInternalLink } from '../../theme'
import DoubleCurrencyLogo from '../DoubleLogo'
import { CAVAX, Token } from '@pangolindex/sdk'
import { ButtonPrimary } from '../Button'
import { DoubleSideStakingInfo, useMinichefPools } from '../../state/stake/hooks'
import { useColor } from '../../hooks/useColor'
import { currencyId } from '../../utils/currencyId'
import { Break, CardNoise, CardBGImage } from './styled'
import { unwrappedToken } from '../../utils/wrappedCurrency'
import { PNG } from '../../constants/tokens'
import { CHAINS } from '../../constants/chains'
import { useTranslation } from 'react-i18next'
import RewardTokens from '../RewardTokens'
import { Box } from '@pangolindex/components'
import { useTokens } from '../../hooks/Tokens'
import { BETA_MENU_LINK } from 'src/constants'
import { useChainId } from 'src/hooks'

const StatContainer = styled.div`
  display: flex;
  justify-content: space-between;
  flex-direction: column;
  gap: 12px;
  margin-bottom: 1rem;
  margin-right: 1rem;
  margin-left: 1rem;
  ${({ theme }) => theme.mediaWidth.upToSmall`
   display: none;
 `};
`

const AprContainer = styled.div`
  display: flex;
  justify-content: space-between;
  flex-direction: column;
  gap: 12px;
  margin-bottom: 1rem;
  margin-right: 1rem;
  margin-left: 1rem;
`

const Wrapper = styled(AutoColumn)<{ showBackground: boolean; bgColor: any }>`
  border-radius: 12px;
  width: 100%;
  overflow: hidden;
  position: relative;
  opacity: ${({ showBackground }) => (showBackground ? '1' : '1')};
  background: ${({ theme, bgColor, showBackground }) =>
    `radial-gradient(91.85% 100% at 1.84% 0%, ${bgColor} 0%, ${showBackground ? theme.black : theme.bg5} 100%) `};
  color: ${({ theme, showBackground }) => (showBackground ? theme.white : theme.text1)} !important;

  ${({ showBackground }) =>
    showBackground &&
    `  box-shadow: 0px 0px 1px rgba(0, 0, 0, 0.01), 0px 4px 8px rgba(0, 0, 0, 0.04), 0px 16px 24px rgba(0, 0, 0, 0.04),
     0px 24px 32px rgba(0, 0, 0, 0.01);`}
`

const TopSection = styled.div<{ isStaking?: boolean }>`
  display: flex;
  justify-content: space-between;
  align-items: center;
  // grid-template-columns: ${({ isStaking }) => (isStaking ? '48px 1fr auto 120px 120px' : '48px 1fr auto 120px')};
  // grid-gap: 0px;
  align-items: center;
  padding: 1rem;
  z-index: 1;
  /* // ${({ theme, isStaking }) => theme.mediaWidth.upToSmall`
  //    grid-template-columns: ${isStaking ? '48px 1fr auto 96px 96px' : ' 48px 1fr auto 96px'};
  //  `}; */
`

const BottomSection = styled.div<{ showBackground: boolean }>`
  padding: 12px 16px;
  opacity: ${({ showBackground }) => (showBackground ? '1' : '0.4')};
  border-radius: 0 0 12px 12px;
  display: flex;
  flex-direction: row;
  align-items: baseline;
  justify-content: space-between;
  z-index: 1;
`

export default function DoubleSidePoolCard({
  stakingInfo,
  version,
  swapFeeApr,
  stakingApr
}: {
  stakingInfo: DoubleSideStakingInfo
  version: string
  swapFeeApr: number
  stakingApr: number
}) {
  const chainId = useChainId()

  const token0 = stakingInfo.tokens[0]
  const token1 = stakingInfo.tokens[1]

  const currency0 = unwrappedToken(token0, chainId)
  const currency1 = unwrappedToken(token1, chainId)

  const poolMap = useMinichefPools()

  const { t } = useTranslation()
  const isStaking = Boolean(stakingInfo.stakedAmount.greaterThan('0'))

  const token: Token =
    currency0 === CAVAX[chainId] || currency1 === CAVAX[chainId]
      ? currency0 === CAVAX[chainId]
        ? token1
        : token0
      : token0.equals(PNG[token0.chainId])
      ? token1
      : token0

  const totalStakedInUsd = CHAINS[chainId].is_mainnet
    ? stakingInfo.totalStakedInUsd.toSignificant(4, { groupSeparator: ',' })
    : 0

  const backgroundColor = useColor(token)

  const pairAddress = stakingInfo?.stakedAmount?.token?.address

  const rewardTokens = useTokens(stakingInfo?.rewardTokensAddress)

  return (
    <Wrapper showBackground={isStaking} bgColor={backgroundColor}>
      <CardBGImage desaturate />
      <CardNoise />

      <TopSection isStaking={isStaking}>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <DoubleCurrencyLogo currency0={currency0} currency1={currency1} size={24} />
          <TYPE.white fontWeight={600} fontSize={24} style={{ marginLeft: '8px' }}>
            {currency0.symbol}-{currency1.symbol}
          </TYPE.white>
        </div>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          {(rewardTokens || [])?.length > 0 && (
            <Box mr={10}>
              <RewardTokens rewardTokens={rewardTokens} size={24} />
            </Box>
          )}

          {/* Beta Migration */}
          {isStaking && Number(version) === 1 && poolMap.hasOwnProperty(pairAddress) ? (
            <StyledInternalLink to={`${BETA_MENU_LINK.migrate}/${version}`} style={{ marginRight: '10px' }}>
              <ButtonPrimary padding="8px" borderRadius="8px">
                Migrate
              </ButtonPrimary>
            </StyledInternalLink>
          ) : null}

          {(isStaking || !stakingInfo.isPeriodFinished) && (
            <StyledInternalLink
              to={`/png/${currencyId(currency0, chainId)}/${currencyId(currency1, chainId)}/${version}`}
              style={{ width: '100%' }}
            >
              <ButtonPrimary padding="8px" borderRadius="8px">
                {isStaking ? t('earn.manage') : t('earn.deposit')}
              </ButtonPrimary>
            </StyledInternalLink>
          )}
        </div>
      </TopSection>

      <StatContainer>
        <RowBetween>
          <TYPE.white> {t('earn.totalStaked')}</TYPE.white>
          <TYPE.white>{totalStakedInUsd ? `$${totalStakedInUsd}` : '-'}</TYPE.white>
        </RowBetween>
      </StatContainer>
      <AprContainer>
        <RowBetween>
          <TYPE.white>Swap Fee APR</TYPE.white>
          <TYPE.white>{swapFeeApr && !stakingInfo.isPeriodFinished ? `${swapFeeApr}%` : '-'}</TYPE.white>
        </RowBetween>
        <RowBetween>
          <TYPE.white>{(rewardTokens || [])?.length === 0 ? 'Farming APR' : 'Super Farm APR'}</TYPE.white>
          <TYPE.white>{stakingApr && !stakingInfo.isPeriodFinished ? `${stakingApr}%` : '-'}</TYPE.white>
        </RowBetween>
        <RowBetween>
          <TYPE.white>Total APR</TYPE.white>
          <TYPE.white>{swapFeeApr && !stakingInfo.isPeriodFinished ? `${swapFeeApr + stakingApr}%` : '-'}</TYPE.white>
        </RowBetween>
      </AprContainer>

      {isStaking && (
        <>
          <Break />
          <Box>
            <BottomSection showBackground={true}>
              <TYPE.black color={'white'} fontWeight={500}>
                <span>{t('earn.yourRate')}</span>
              </TYPE.black>

              <TYPE.black style={{ textAlign: 'right' }} color={'white'} fontWeight={500}>
                <span role="img" aria-label="wizard-icon" style={{ marginRight: '0.5rem' }}>
                  ⚡
                </span>
                {`${stakingInfo.rewardRatePerWeek?.toSignificant(4, { groupSeparator: ',' })} PNG / week`}
              </TYPE.black>
            </BottomSection>

            {(stakingInfo?.rewardTokensAddress || []).length > 0 && (rewardTokens || []).length > 0 && (
              <BottomSection showBackground={true}>
                <TYPE.black color={'white'} fontWeight={500}>
                  <span>{t('earn.extraReward')}</span>
                </TYPE.black>

                <AutoColumn gap="sm">
                  {(rewardTokens || []).map((token, index) => {
                    const tokenMultiplier = stakingInfo?.rewardTokensMultiplier?.[index]
                    const weeklyExtraRewardRate =
                      stakingInfo?.getExtraTokensWeeklyRewardRate &&
                      stakingInfo?.getExtraTokensWeeklyRewardRate(
                        stakingInfo?.rewardRatePerWeek,
                        token as Token,
                        tokenMultiplier
                      )

                    return (
                      <TYPE.black style={{ textAlign: 'right' }} color={'white'} fontWeight={500} key={index}>
                        <span role="img" aria-label="wizard-icon" style={{ marginRight: '0.5rem' }}>
                          ⚡
                        </span>
                        {`${weeklyExtraRewardRate?.toSignificant(4, { groupSeparator: ',' })} ${token?.symbol} / week`}
                      </TYPE.black>
                    )
                  })}
                </AutoColumn>
              </BottomSection>
            )}
          </Box>
        </>
      )}
    </Wrapper>
  )
}
